mapper.select:
select m.menu_id,m.parent_id,m.name,m.url,m.perms,m.type,m.type,m.icon,m.order_num
	from sys_menu m,sys_role_menu rm,sys_user_role ur where rm.role_id = ur.role_id
	and m.parent_id = #{parentId} 

		and ur.user_id = #{userId}

order by m.order_num asc
group by m.menu_id,m.parent_id,m.name,m.url,m.perms,m.type,m.type,m.icon,m.order_num

select m.*,(select p.name from sys_menu p where p.menu_id = m.parent_id) as parentName from sys_menu m
order by m.${sidx} ${order}
order by m.order_num asc

select m.*,(select p.name from sys_menu p where p.menu_id = m.parent_id) as parentName 
	from sys_menu m


		and m.parent_id = #{parentId}




		order by m.${sidx} ${order}


		order by m.order_num asc



	limit #{offset}, #{limit}

mapper.delete:
delete from sys_menu where menu_id in 

	#{menuId}

;
delete from sys_role_menu where menu_id in 

	#{menuId}
